#vomnibar {
  --background: hsl(0 0 12 / 72%);
  --foreground: hsl(0 0 92);
  --foreground-dim: hsl(0 0 60);
  --highlight: hsl(0 0 100 / 15%);

  --border: 1px solid var(--highlight);
  --sonoma-blur: blur(32px) saturate(128%);
  --sonoma-shadow: 0px 16px 32px 8px hsl(0 0 0 / 32%);

  box-sizing: border-box;
  max-width: 700px;
  margin: 3% auto;
  left: 0;
  right: 0;
  background: transparent;
  padding: 0 10px;
  border: unset;
  box-shadow: unset;

  &::before {
    z-index: -1;
    content: "";
    position: absolute;
    inset: 0;
    background: var(--background);
    backdrop-filter: var(--sonoma-blur);
    outline: var(--border);
    outline-offset: -2px;
    box-shadow: var(--sonoma-shadow);
  }

  &,
  &::before {
    border-radius: 12px;
  }

  #vomnibar-search-area {
    padding: unset;
    border: unset;
  }

  input {
    background: unset;
    box-shadow: unset;
    height: unset;
    padding: 16px 10px;
    font-size: 16px;
    color: var(--foreground);
  }

  ul {
    border-top: var(--border);
    padding: 10px 0;
  }

  li {
    display: flex;
    font-size: 14px;
    font-weight: 500;
    color: var(--foreground);
    padding: 10px;
    border: unset;
    border-radius: 8px;

    &.selected {
      background: var(--highlight);
    }

    .top-half {
      flex-shrink: 0;
      max-width: 60%;
    }

    .bottom-half {
      color: var(--foreground-dim);
      font-size: unset;

      &::before {
        content: "â€”";
        margin: 0 2px 0 6px;
      }
    }

    .top-half,
    .bottom-half {
      padding: 4px 0;
      text-wrap: nowrap;
    }

    .title,
    .bottom-half {
      margin: unset;
    }

    .source {
      display: none;
    }

    .icon {
      padding: 0 5px 0 0;
    }

    .title,
    .title .match,
    .url,
    .url .match {
      color: unset;
    }
  }
}
